def is_q_of_the_form_1_plus_2_l_j(q, l):
    if q <= _sage_const_1 :
        return False
    diff = q - _sage_const_1
    if diff % _sage_const_2 != _sage_const_0:
        return False
    diff //= _sage_const_2
    if diff == _sage_const_0:
        return False
    while diff % l == _sage_const_0:
        diff //= l
    return diff == _sage_const_1

